CATCreateClass( ModelCredibilityCmd);


//-------------------------------------------------------------------------
// Constructor
//-------------------------------------------------------------------------
ModelCredibilityCmd::ModelCredibilityCmd() :
  CATStateCommand ("ModelCredibilityCmd", CATDlgEngOneShot, CATCommandModeExclusive) 
//  Valid states are CATDlgEngOneShot and CATDlgEngRepeat
, pOkBut(NULL), pCancelBut(NULL), pCloseBut(NULL)
{
	pDlgModelCredibility = new CDlgModelCredibility();
	pDlgModelCredibility->Build();
	pDlgModelCredibility->SetVisibility(CATDlgShow);
}

//-------------------------------------------------------------------------
// Destructor
//-------------------------------------------------------------------------
ModelCredibilityCmd::~ModelCredibilityCmd()
{
	if (pOkBut != NULL)
	{
		pOkBut->RequestDelayedDestruction();
		pOkBut = NULL;
	}
	if (pCancelBut != NULL)
	{
		pCancelBut->RequestDelayedDestruction();
		pCancelBut = NULL;
	}
	if (pCloseBut != NULL)
	{
		pCloseBut->RequestDelayedDestruction();
		pCloseBut = NULL;
	}
	if (pDlgModelCredibility != NULL) 
	{
		pDlgModelCredibility->RequestDelayedDestruction();
		pDlgModelCredibility = NULL;
	}
}


//-------------------------------------------------------------------------
// BuildGraph()
//-------------------------------------------------------------------------
void ModelCredibilityCmd::BuildGraph()
{


  // TODO: Define the StateChart 
  // ---------------------------
	pOkBut = new CATDialogAgent("OK");
	pCloseBut = new CATDialogAgent("Close");
	pCancelBut = new CATDialogAgent("Cancel");

	pOkBut->AcceptOnNotify(pDlgModelCredibility,pDlgModelCredibility->GetDiaOKNotification());
	pCancelBut->AcceptOnNotify(pDlgModelCredibility,pDlgModelCredibility->GetWindCloseNotification());
	pCloseBut->AcceptOnNotify(pDlgModelCredibility,pDlgModelCredibility->GetDiaCANCELNotification());

	CATDialogState* pState = GetInitialState("OK");
	pState->AddDialogAgent(pOkBut);
	pState->AddDialogAgent(pCloseBut);
	pState->AddDialogAgent(pCancelBut);

	AddTransition(pState,NULL,IsOutputSetCondition(pOkBut),Action((ActionMethod)&ModelCredibilityCmd::ActionOK));
	AddTransition(pState,NULL,IsOutputSetCondition(pCloseBut),Action((ActionMethod)&ModelCredibilityCmd::ActionClose));
	AddTransition(pState,NULL,IsOutputSetCondition(pCancelBut),Action((ActionMethod)&ModelCredibilityCmd::ActionCancel));
}


