#include "ModelPropertiesCmd.h"
#include "CATMathPlane.h"

#include "CATCreateExternalObject.h"
CATCreateClass( ModelPropertiesCmd);


//-------------------------------------------------------------------------
// Constructor
//-------------------------------------------------------------------------
ModelPropertiesCmd::ModelPropertiesCmd() :
  CATStateCommand ("ModelPropertiesCmd", CATDlgEngOneShot, CATCommandModeExclusive) 
//  Valid states are CATDlgEngOneShot and CATDlgEngRepeat
, pOkBut(NULL), pCancelBut(NULL), pCloseBut(NULL)
{
	pDlgModelProperties = new CDlgModelProperties();
	pDlgModelProperties->Build();
	pDlgModelProperties->SetVisibility(CATDlgShow);
}

//-------------------------------------------------------------------------
// Destructor
//-------------------------------------------------------------------------
ModelPropertiesCmd::~ModelPropertiesCmd()
{
	if (pOkBut != NULL)
	{
		pOkBut->RequestDelayedDestruction();
		pOkBut = NULL;
	}
	if (pCancelBut != NULL)
	{
		pCancelBut->RequestDelayedDestruction();
		pCancelBut = NULL;
	}
	if (pCloseBut != NULL)
	{
		pCloseBut->RequestDelayedDestruction();
		pCloseBut = NULL;
	}
	if (pDlgModelProperties != NULL) 
	{
		pDlgModelProperties->RequestDelayedDestruction();
		pDlgModelProperties = NULL;
	}
}


//-------------------------------------------------------------------------
// BuildGraph()
//-------------------------------------------------------------------------
void ModelPropertiesCmd::BuildGraph()
{


  // TODO: Define the StateChart 
  // ---------------------------
	pOkBut = new CATDialogAgent("OK");
	pCloseBut = new CATDialogAgent("Close");
	pCancelBut = new CATDialogAgent("Cancel");

	pOkBut->AcceptOnNotify(pDlgModelProperties,pDlgModelProperties->GetDiaOKNotification());
	pCancelBut->AcceptOnNotify(pDlgModelProperties,pDlgModelProperties->GetWindCloseNotification());
	pCloseBut->AcceptOnNotify(pDlgModelProperties,pDlgModelProperties->GetDiaCANCELNotification());

	CATDialogState* pState = GetInitialState("OK");
	pState->AddDialogAgent(pOkBut);
	pState->AddDialogAgent(pCloseBut);
	pState->AddDialogAgent(pCancelBut);

	AddTransition(pState,NULL,IsOutputSetCondition(pOkBut),Action((ActionMethod)&ModelPropertiesCmd::ActionOK));
	AddTransition(pState,NULL,IsOutputSetCondition(pCloseBut),Action((ActionMethod)&ModelPropertiesCmd::ActionClose));
	AddTransition(pState,NULL,IsOutputSetCondition(pCancelBut),Action((ActionMethod)&ModelPropertiesCmd::ActionCancel));
}


