void PathManagerAddin::CreateCommands()
{
	new PathManagerHeader( "HidePath","PathManager","HidePathCmd",(void *)NULL);
}

CATCmdContainer* PathManagerAddin::CreateToolbars()
{

	NewAccess        (CATCmdContainer,pToolbar,PathManager);

	NewAccess        (CATCmdStarter,pHidePath,HidePathCmd);
	SetAccessCommand (pHidePath,"HidePath");
	SetAccessChild   (pToolbar , pHidePath);

	AddToolbarView   (pToolbar,1,Right); // 1 for visible toolbar

	return pToolbar;
}
