#include "AddSubtitleCmd.h"
#include "CATAppSettings.h"

#include "CATCreateExternalObject.h"
CATCreateClass( AddSubtitleCmd);


//-------------------------------------------------------------------------
// Constructor
//-------------------------------------------------------------------------
AddSubtitleCmd::AddSubtitleCmd() :
  CATStateCommand ("AddSubtitleCmd", CATDlgEngOneShot, CATCommandModeExclusive) 
//  Valid states are CATDlgEngOneShot and CATDlgEngRepeat
, pOkBut(NULL), pCancelBut(NULL), pCloseBut(NULL)
{
	CATAppSettings::GetSettings();
	pDlgAddSubtitle = new CDlgAddSubtitle();
	pDlgAddSubtitle->Build();
	pDlgAddSubtitle->SetVisibility(CATDlgShow);
}

//-------------------------------------------------------------------------
// Destructor
//-------------------------------------------------------------------------
AddSubtitleCmd::~AddSubtitleCmd()
{
	if (pOkBut != NULL)
	{
		pOkBut->RequestDelayedDestruction();
		pOkBut = NULL;
	}
	if (pCancelBut != NULL)
	{
		pCancelBut->RequestDelayedDestruction();
		pCancelBut = NULL;
	}
	if (pCloseBut != NULL)
	{
		pCloseBut->RequestDelayedDestruction();
		pCloseBut = NULL;
	}
	if (pDlgAddSubtitle != NULL) 
	{
		pDlgAddSubtitle->RequestDelayedDestruction();
		pDlgAddSubtitle = NULL;
	}
}

//-------------------------------------------------------------------------
// BuildGraph()
//-------------------------------------------------------------------------
void AddSubtitleCmd::BuildGraph()
{
	pOkBut = new CATDialogAgent("OK");
	pCloseBut = new CATDialogAgent("Close");
	pCancelBut = new CATDialogAgent("Cancel");

	pOkBut->AcceptOnNotify(pDlgAddSubtitle,pDlgAddSubtitle->GetDiaOKNotification());
	pCancelBut->AcceptOnNotify(pDlgAddSubtitle,pDlgAddSubtitle->GetWindCloseNotification());
	pCloseBut->AcceptOnNotify(pDlgAddSubtitle,pDlgAddSubtitle->GetDiaCANCELNotification());

	CATDialogState* pState = GetInitialState("OK");
	pState->AddDialogAgent(pOkBut);
	pState->AddDialogAgent(pCloseBut);
	pState->AddDialogAgent(pCancelBut);

	AddTransition(pState,NULL,IsOutputSetCondition(pOkBut),Action((ActionMethod)&AddSubtitleCmd::ActionOK));
	AddTransition(pState,NULL,IsOutputSetCondition(pCloseBut),Action((ActionMethod)&AddSubtitleCmd::ActionClose));
	AddTransition(pState,NULL,IsOutputSetCondition(pCancelBut),Action((ActionMethod)&AddSubtitleCmd::ActionCancel));
}

CATBoolean AddSubtitleCmd::ActionOK( void *data )
{
	// TODO: Define the action associated with the transition 
	// ------------------------------------------------------
	if (pDlgAddSubtitle)
	{
		pDlgAddSubtitle->RequestDelayedDestruction();
		pDlgAddSubtitle = NULL;
	}
	return TRUE;
}

